example.programlisting:
/* create the standard screen */
scr = .Window~new()

/* create a new window */
win = .Window~new(10, 20, 5, 10)

numeric digits 12 lf = .window~ASCII_LF~d2c()
scr = .window~new()
/* Build window & wait */ fred = .window~new(0,0,1,1) fred~addstr("This is the original window, Fred." || lf) fred~refresh() fred~getch()
/* Create and show barney */ barney = fred~dupwin()
