section.programlisting:
/* ------------------------------------------------------------------------- */
::method defineDialog
/* ------------------------------------------------------------------------- */
/* define DlgArea named u as whole of user dialog                            */
u=.dlgArea~new(  0       ,         0,self~SizeX,Self~SizeY)   /* whole dlg   */

/* define DlgArea named e within DlgArea u for edit control                  */
e=.dlgArea~new(u~x       ,u~y       ,u~w("70%"),u~h("90%"))

/* define DlgArea named s within DlgArea u for Status Area in remaining height*/
s=.dlgArea~new(u~x       ,u~y("90%"),u~w("70%"),u~hr      )

/* define DlgArea named b within DlgArea u for Button area                   */
b=.dlgArea~new(u~x("70%"),u~y       ,u~wr      ,u~hr      )

/* Edit control coterminous with area e margins                              */
self~createEdit(12, e~x, e~y, e~w, e~h, "multiline", "text")

/* Status Area Coterminous with area s margins                               */
self~createStaticText(11, s~x, s~y, s~w, s~h, , "Status info appears here")

/* Seven buttons evenly spaced at 10% intervals, 9% high                     */
do i = 0 to 6
   self~createPushButton(12+i,b~x,b~y((i * 10)||"%"),b~w,b~h("9%"), ,"Button" i,"Button"||i)
end /* DO */

/* ok button 15 dialog units high at bottom of area b                        */
self~createPushButton(IDOK,b~x,b~y(-15),b~w,15,"Default","OK","OK")

/* DlgAreaDemo.Rex -- Demonstrate DlgArea & DlgAreaU Classes -- Feb 2006 */
dlg = .MyDialog~new if dlg~initCode == 0 then dlg~execute("ShowTop")
return dlg~initCode
::requires "ooDialog.cls" /* ========================================================================= */ ::class 'MyDialog' subclass UserDialog /* ========================================================================= */ ::method init /* ------------------------------------------------------------------------- */ self~init:super if \ self~createCenter(250, 250, "MyDialog", "ThickFrame", , "Tahoma", 8) then do self~initCode = 1 return end
self~connectResize("onResize")
/* ------------------------------------------------------------------------- */ ::method defineDialog /* ------------------------------------------------------------------------- */ expose u
u = .dlgAreaU~new(self) /* whole dlg */ if u~lastError \= .nil then call errorDialog u~lastError e = .dlgArea~new(u~x ,u~y ,u~w("70%"),u~h("90%")) /* edit area */ s = .dlgArea~new(u~x ,u~y("90%"),u~w("70%"),u~hr ) /* status area */ b = .dlgArea~new(u~x("70%"),u~y ,u~wr ,u~hr ) /* button area */
self~createEdit(IDC_EDIT, e~x, e~y, e~w, e~h, "multiline", "text") self~createStaticText(IDC_ST_STATUS, s~x, s~y, s~w, s~h, , "Status info appears here")
self~createPushButton(IDC_PB_0, b~x, b~y('00%'), b~w, b~h('9%'), ,'Button' 0, 'Button'||0) self~createPushButton(IDC_PB_1, b~x, b~y('10%'), b~w, b~h('9%'), ,'Button' 1, 'Button'||1) self~createPushButton(IDC_PB_2, b~x, b~y('20%'), b~w, b~h('9%'), ,'Button' 2, 'Button'||2) self~createPushButton(IDC_PB_3, b~x, b~y('30%'), b~w, b~h('9%'), ,'Button' 3, 'Button'||3) self~createPushButton(IDC_PB_4, b~x, b~y('40%'), b~w, b~h('9%'), ,'Button' 4, 'Button'||4) self~createPushButton(IDC_PB_5, b~x, b~y('50%'), b~w, b~h('9%'), ,'Button' 5, 'Button'||5) self~createPushButton(IDC_PB_6, b~x, b~y('60%'), b~w, b~h('9%'), ,'Button' 6, 'Button'||6) self~createPushButton(IDOK, b~x, b~y('90%'), b~w, b~h('9%'),'DEFAULT', 'Ok')
/* ------------------------------------------------------------------------- */ ::method unknown /* ------------------------------------------------------------------------- */ use arg msgname, args if msgname~abbrev("BUTTON") then self~newStatic(IDC_ST_STATUS)~setText('You Pressed Button' msgname~right(1))
/* ------------------------------------------------------------------------- */ ::method onResize /* ------------------------------------------------------------------------- */ expose u use arg sizeEvent, sizeinfo u~resize(self, sizeinfo)
